package com.leet.daysofcode.year2020.november.week4;

import java.util.stream.IntStream;

public class LongestSubstringWithAtLeastKRepeatingCharacters {

    public static void main(String[] args) {
        String[] inputs = {"aaabb", "ababbc", "aaabbb", ""};
        int[] ks = {3, 2, 3, 301};
        int[] outputs = {3, 5, 6, 301};
        LongestSubstringWithAtLeastKRepeatingCharacters longestSubstringWithAtLeastKRepeatingCharacters = new LongestSubstringWithAtLeastKRepeatingCharacters();
        IntStream.range(0, inputs.length).forEachOrdered(i -> {
            System.out.println("Input : " + inputs[i]);
            System.out.println("Output : " + longestSubstringWithAtLeastKRepeatingCharacters.longestSubstring(inputs[i], ks[i]));
            System.out.println("Expected : " + outputs[i]);
        });
    }

    public int longestSubstring(String s, int k) {
        if (s == null || s.trim().length() == 0) {
            return 0;
        }
        if (k == 1) {
            return s.length();
        }
        s = s.trim();
        int len = s.length();
        int max = 0;
        for (int window = k; window <= len; window++) {
            int[] alpha = new int[26];
            for (int j = 0; j + window <= len; j++) {
                int start = j;
                int end = j + window;
                if (j == 0) {
                    for (int index = start; index < end; index++) {
                        alpha[s.charAt(index) - 'a']++;
                    }
                } else {
                    alpha[s.charAt(end - 1) - 'a']++;
                    alpha[s.charAt(start - 1) - 'a']--;
                }
                if (isMinGreaterThanK(alpha, k)) {
                    max = Math.max(max, window);
                }
            }
        }
        return max;
    }

    public boolean isMinGreaterThanK(int[] arr, int k) {
        for (int i : arr) {
            if (i != 0 && i < k) {
                return false;
            }
        }
        return true;
    }


}
